import React, { useState, useEffect } from "react";
import useAuth from "./useAuth";
import SpotifyWebApi from "spotify-web-api-node";
import TrackSearchResult from "./TrackSearchResult";

const spotifyApi = new SpotifyWebApi({
  clientId: "1a9068e5a02c4897adb142f295edfe75",
});

const MainPage = ({ code }) => {
  const accessToken = useAuth(code);
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);
console.log(searchResults)
  // whenever accesstoken changes, set token on spotifyAPI
  useEffect(() => {
    if (!accessToken) return
    spotifyApi.setAccessToken(accessToken);
  }, [accessToken]);

  // for searching
  useEffect(() => {
    if (!search) return setSearchResults([]);
    if (!accessToken) return;

    spotifyApi.searchTracks(search).then((res) => {
      //info i want: artist name, album image, track name, URI
      setSearchResults(res.body.tracks.items.map(track => {
        // compares the image height 
        const smallestAlbumImage = track.album.images.reduce((smallest, image) => {
          if (image.height < smallest.height) return image
          return smallest
        }, track.album.images[0])

        return {
          artist: track.artists[0].name,
          title: track.name,
          uri: track.uri,
          albumUrl: smallestAlbumImage.url
        }
      }))
    });
  }, [search, accessToken]);

  return (
    <div className="container">
      <form>
        <input
          className="searchbox"
          type="search"
          placeholder="Search by Songs/Artists"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <div className="songlist">
          hello
          {searchResults.map(track=>{
            <TrackSearchResult track={track} key={track.uri} />
          })}

          </div>
        <div>Bottom</div>
      </form>
    </div>
  );
};

export default MainPage;
