import React, {useState} from "react";
import { useSelector, useDispatch } from "react-redux";
import {userActions} from "../store/main";

const Auth = () => {
    const dispatch = useDispatch()
    const storeAuth = useSelector((state)=> state.auth);
    const storeUsername = useSelector((state) => state.username)

    const [username, setUsername] = userState("");

    const loginUser=()=>{
        dispatch(userActions.login(username));
    };
    const logoutUser=()=>{
        dispatch(userActions,logout());
    };

    const handleUsernameChange=(e)=>{
        setUsername(e.target.value);
    };

    return <div className="container">
        <div className="row">
            <div className="col-md-4">
                <label htmlFor="username">Username</label>
            </div>
            <div className="col-md-8">
                <input id='username' type='text' onChange={handleUsernameChange}>
                </input>
            </div>
        </div>
        <div className="row">
            <div className="col-md-12">
                <button onClick={loginUser} type='submit'>
                    Login
                </button>
                <button onClick={logoutUser} type='submit'>
                    Logout
                </button>
            </div>
        </div>
        {storeAuth ? "logged in" : "logged out"}
        <br />
        {storeUsername}
    </div>
};

export default Auth;
