        console.log(e.target.value)
      }
    return (
        <div className="searchbox">
            <input 
            value={props.value} 
            placeholder="Enter a Movie Title"
            onChange={(event)=>props.setQuery(event.target.value)}
            >
            </input>
        </div>
    );
};

