import React, { useState, useEffect } from "react";
import useAuth from "./useAuth";
import SpotifyWebApi from "spotify-web-api-node";
import TrackSearchResult from "./TrackSearchResult";
import MusicPlayer from "./MusicPlayer";
import axios from "axios";
import FadeIn from "react-fade-in";
import UserProfile from "./UserProfile";
import RecentlyPlayed from "./RecentlyPlayed";
const spotifyApi = new SpotifyWebApi({
  clientId: "1a9068e5a02c4897adb142f295edfe75",
});
const OAuth =
  "BQCyyWCMzxyJmIBrI8YJFrMFVxkCnFBzO_REMsSUux8dJqj8xf73yHwFyv_dmiH53SF6dUG90vTM5U48_gopjm567ahaS3MF9Voo7n8mCRhnQR-jpb3FCDtltGjI2DPFf9OL1Q5sd5s8c49v2dsX";
const MainPage = ({ code }) => {
  const accessToken = useAuth(code);
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [currentTrack, setCurrentTrack] = useState();
  const [lyrics, setLyrics] = useState();
  const [showGreeting, setShowGreeting] = useState(true);
  const [userInfo, setUserInfo] = useState(false);
  const [recentlyPlayed, setRecentlyPlayed] = useState([]);
  const chooseTrack = (track) => {
    setCurrentTrack(track);
    // when choosing track, empty the search
    setSearch("");
    setLyrics("");
  };

  useEffect(()=>{
    if (!accessToken) return;
    spotifyApi.getMyRecentlyPlayedTracks({ limit: 20 }).then((res) => {
      setRecentlyPlayed(
        res.body.items.map(({track}) => {
          return {
            id: track.id,
            artist: track.artists[0].name,
            title: track.name,
            uri: track.uri,
            albumUrl: track.album.images[0].url,
          };
        })
      )
    })
  }, [accessToken])

  // Get recently played tracks 
  useEffect(() => {
    if (!accessToken) return;
    const fetchRecentlyPlayed = () => {
      axios
        .get("https://api.spotify.com/v1/me/player/recently-played?limit=10", {
          headers: {
            Authorization: `Bearer ${OAuth}`,
            "Content-Type": "application/json",
          },
        })
        .then((res) => {
          const recentlyplayedlist = res.data.items;
          console.log(recentlyplayedlist);
          setRecentlyPlayed(recentlyplayedlist);
        });
    };
    fetchRecentlyPlayed();
  }, [accessToken]);

  useEffect(() => {
    if (!accessToken) return;
    const fetchUserInfo = () => {
      axios
        .get("https://api.spotify.com/v1/me", {
          headers: {
            Authorization: `Bearer ${accessToken}`,
            "Content-Type": "application/json",
          },
        })
        .then((res) => {
          // console.log(res.data);
          setUserInfo(res.data);
        });
    };
    fetchUserInfo();
  }, [accessToken]);

  useEffect(() => {
    setInterval(() => {
      setShowGreeting(!showGreeting);
    }, 3000);
  }, []);

  useEffect(() => {
    if (!currentTrack) return;

    axios
      .get("http://localhost:3001/lyrics", {
        params: {
          track: currentTrack.title,
          artist: currentTrack.artist,
        },
      })
      .then((res) => {
        setLyrics(res.data.lyrics);
      });
  }, [currentTrack]);
  // console.log(searchResults);

  // whenever accesstoken changes, set token on spotifyAPI
  useEffect(() => {
    if (!accessToken) return;
    spotifyApi.setAccessToken(accessToken);
  }, [accessToken]);

  useEffect(() => {
    if (!search) return setSearchResults([]);
    if (!accessToken) return;

    let cancel = false;
    spotifyApi.searchTracks(search).then((res) => {
      if (cancel) return;
      //info i want: artist name, album image, track name, URI
      setSearchResults(
        res.body.tracks.items.map((track) => {
          // compares the image height and grabs the smallest image
          const smallestAlbumImage = track.album.images.reduce(
            (smallest, image) => {
              if (image.height < smallest.height) return image;
              return smallest;
            },
            track.album.images[0]
          );

          return {
            artist: track.artists[0].name,
            title: track.name,
            uri: track.uri,
            albumUrl: smallestAlbumImage.url,
          };
        })
      );
    });
    return () => (cancel = true);
  }, [search, accessToken]);

  return (
    <div className="container">
      <div className="container2"></div>
      <FadeIn>
        <form>
          <input
            className="searchbox"
            type="search"
            placeholder="Search Tracks by Songs/Artists"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <div className="searchresultslist">
            {searchResults.map((track) => (
              <TrackSearchResult
                track={track}
                key={track.uri}
                chooseTrack={chooseTrack}
              />
            ))}
            {searchResults.length === 0 && (
              <div className="lyricscontainer">{lyrics}</div>
            )}
          </div>
          {/* passing in the access token to enable playback fn*/}
          <div>
            <MusicPlayer
              trackUri={currentTrack?.uri}
              accessToken={accessToken}
            />
          </div>
        </form>
      </FadeIn>
      <UserProfile userInfo={userInfo} />

      <RecentlyPlayed accessToken={accessToken} recentlyPlayed={recentlyPlayed} />
    </div>
  );
};

export default MainPage;
