import React, { useState, useEffect } from "react";
import useAuth from "./useAuth";
import SpotifyWebApi from "spotify-web-api-node";

const spotifyApi = new SpotifyWebApi({
  clientId: "1a9068e5a02c4897adb142f295edfe75",
});

const MainPage = ({ code }) => {
  const accessToken = useAuth(code);
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  // whenever accesstoken changes, set token on spotifyAPI
  useEffect(() => {
    if (!accessToken) return
    spotifyApi.setAccessToken(accessToken);
  }, [accessToken]);

  // for searching
  useEffect(() => {
    if (!search) return setSearchResults([]);
    if (!accessToken) return;

    spotifyApi.searchTracks(search).then((res) => {
      //info i want: artist name, album image, track name, URI
      console.log(res.body.tracks.items);
    });
  }, [search, accessToken]);

  return (
    <div className="container">
      <form>
        <input
          className="searchbox"
          type="search"
          placeholder="Search by Songs/Artists"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <div className="songlist">Songs</div>
        <div>Bottom</div>
      </form>
    </div>
  );
};

export default MainPage;
