import React, { useState } from "react";
import Parent from "./components/Parent";
import Child from "./components/Child";
import ChickenContext from "./context/chicken-context";

function App() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("something");

  return (
    // anything within these 2 lines can use this context
  <ChickenContext.Provider
  value={{ username, setUsername, email, setEmail }}
  >
     <div className="container">
       <h2>Parent:</h2>
       <Parent />
       <h2>Child:</h2>
       <Child />
       </div>
    </ChickenContext.Provider>
  );
}

export default App;
