import React, {useEffect, useState} from "react";
import Home from "./components/Home";
import Result from "./components/Result";
import results from "./components/results";


// import MovieList from "./components/MovieList"
import SearchBox from "./components/SearchBox";
function App() {
  const [movies, setMovies] = useState([]);
  const [searchValue, setSearchValue] = useState('');
  const [names, setNames] = useState([]);

  const getMovieRequest = async (searchValue) => {
    const url = `https://api.tvmaze.com/search/shows?q=${searchValue}`

    const response = await fetch(url);
    const responseJson = await response.json();

    console.log(url);

    const filtered = responseJson.map((movie)=>
      movie.show.image.medium
    )
    const filtered2 = responseJson.map((movie2)=>
    movie2.show.name)


    console.log(filtered)  
    console.log(filtered2)
    // if (responseJson.show.name){
    //   setMovies(responseJson.show.name);
    // }
    setMovies(filtered)
    setNames(filtered2)
  };

  useEffect(()=>{
    getMovieRequest(searchValue);
  }, [searchValue]);

  return (
    <div>
      <Home />
      <SearchBox searchValue={searchValue} setSearchValue={setSearchValue}/>
      <div>
      <Result  names={names} movies={movies}/>
      </div>
    </div>
  );
}

export default App;
